import React from 'react'
import { Row, Col } from 'react-bootstrap'
import Products from '../products'
import ProductScreen from './ProductScreen'
export default function HomeScreen() {
  return (
    <>
      <Row>
        {
          Products.map(() => {
            <>
              <Col>
                <ProductScreen Product={Products} />
              </Col>
            </>
          })
        }
      </Row>
    </>
  )
}
