import React from 'react'
import { Card } from 'react-bootstrap'

export default function ProductScreen({}) {
  return (
    <>
        <Card className='my-3 p-3 rounded'>
            <a href={`/Product/${Product._id}`}>
                {/* <Card.Img src={Product.image} variant='top'/> */}
                <h1>{Product.name}</h1>
            </a>
        </Card>
    </>
  )
}
